mapper.select:

SELECT FAQ_ID, TITLE, CONTENT
FROM(        
      SELECT /*+INDEX_DESC(FAQ PK_FAQ) */ 
      ROWNUM AS RN, FAQ_ID, TITLE, CONTENT
      FROM FAQ 
      WHERE ROWNUM <= #{pageNum} * #{amount}


	  )
	WHERE RN > (#{pageNum} -1) * #{amount}

mapper.update:
UPDATE FAQ
SET TITLE = #{TITLE},
CONTENT = #{CONTENT}
WHERE FAQ_ID = #{FAQ_ID}
